import React from "react";
import { PanelBar, PanelBarItem } from "@progress/kendo-react-layout";

import { getFundInfo, getModelPortfolio } from "../services/dataService";
import Loading from "../layout/Loading";
import { FundInfo, ModelPortfolio } from "../data/models";

export default function PortfolioPanel() {
  const [fundInfo, setFundInfo] = React.useState<FundInfo>();
  const [modelPortfolio, setModelPortfolio] = React.useState<ModelPortfolio>();
  React.useEffect(() => {
    getFundInfo().then((data: FundInfo) => {
      setFundInfo(data);
    });
    getModelPortfolio().then((data: ModelPortfolio) => {
      setModelPortfolio(data);
    });
  }, []);

  return (
    <>
      {!modelPortfolio && <Loading />}
      <PanelBar style={{ opacity: modelPortfolio ? "1" : "0" }}>
        <PanelBarItem expanded={true} title="Model Portfolios">
          <div>
            {modelPortfolio &&
              modelPortfolio.modelPortfolio.map((item, idx) => (
                <div className="modelPortfolio" key={idx}>
                  <span className="modelPortfolio">
                    <h2>{item.Name}</h2>
                    <p>{item.Position}</p>
                  </span>
                </div>
              ))}
          </div>
        </PanelBarItem>
        <PanelBarItem title={"Portfolio Details"}>
          {fundInfo &&
            fundInfo.quarters.map((quarter, idx) => (
              <PanelBarItem title={quarter.title} key={idx}>
                <ul className="fund-detail-list">
                  {quarter.details.map((detail, index) => (
                    <li key={index}>
                      <span>{detail.name}:</span>
                      <span>{detail.value}</span>
                    </li>
                  ))}
                </ul>
              </PanelBarItem>
            ))}
        </PanelBarItem>
      </PanelBar>
    </>
  );
}
