import React from "react";
import { PanelBar, PanelBarItem } from "@progress/kendo-react-layout";

import { getModelPortfolio } from "../services/dataService";
import Loading from "../layout/Loading";
import { ModelPortfolio } from "../data/models";

import Select from "react-select";

export default function PortfolioPanel() {
  const [modelPortfolio, setModelPortfolio] = React.useState<ModelPortfolio>();
  React.useEffect(() => {
    getModelPortfolio().then((data: ModelPortfolio) => {
      setModelPortfolio(data);
    });
  }, []);

  const options = [
    { value: "chocolate", label: "Chocolate" },
    { value: "strawberry", label: "Strawberry" },
    { value: "vanilla", label: "Vanilla" },
  ];

  return (
    <>
      {!modelPortfolio && <Loading />}
      <PanelBar style={{ opacity: modelPortfolio ? "1" : "0" }}>
        <PanelBarItem expanded={true} title="Model Portfolios">
          <Select defaultValue={"chocolate"} options={options} />
        </PanelBarItem>
        <PanelBarItem expanded={true} title="Model Portfolios">
          <div>
            {modelPortfolio &&
              modelPortfolio.modelPortfolio.map((item, idx) => (
                <div className="model-portfolio" key={idx}>
                  <span className="model-portfolio-info">
                    <h2>{item.Name}</h2>
                  </span>
                </div>
              ))}
          </div>
        </PanelBarItem>
      </PanelBar>
    </>
  );
}
