import React from "react";
import { PanelBar, PanelBarItem } from "@progress/kendo-react-layout";

import { getModelPortfolio } from "../services/dataService";
import Loading from "../layout/Loading";
import { ModelPortfolio } from "../data/models";

import { DropDownList } from "@progress/kendo-react-dropdowns";
import { Grid, GridCellProps, GridColumn } from "@progress/kendo-react-grid";

export default function PortfolioPanel() {
  const [modelPortfolio, setModelPortfolio] = React.useState<ModelPortfolio>();
  const defaultName = "select a portfolio..";
  const [portfolioName, setPortfolioName] = React.useState<String>(defaultName);
  React.useEffect(() => {
    getModelPortfolio().then((data: ModelPortfolio) => {
      setModelPortfolio(data);
    });
  }, []);

  if (!modelPortfolio) return <Loading />;
  const modelPortfolios = modelPortfolio?.modelPortfolio;

  const portfolioNames = modelPortfolios.keys();

  let positions = [
    { Ticker: "SPY", Percentage: 0.6, Asset: "Equity" },
    { Ticker: "BND", Percentage: 0.4, Asset: "Fixed Income" },
  ];
  return (
    <PanelBar style={{ opacity: modelPortfolio ? "1" : "0" }}>
      <PanelBarItem expanded={true} title="Select a model portfolio">
        <DropDownList
          className="drop-down-list"
          data={portfolioNames}
          defaultItem={defaultName}
          onChange={(event) => {
            setPortfolioName(event.value);
            for (const val of modelPortfolios) {
              if ((val.Name = event.value)) {
                positions = val.Position;
              }
            }
          }}
        />
        <Grid data={positions} style={{ opacity: positions ? "1" : "0" }}>
          <GridColumn title="Asset" field="Asset" locked={true} width={100} />
          <GridColumn title="Ticker" field="Ticker" />
          <GridColumn title="Percentage" field="Percentage" />
        </Grid>
      </PanelBarItem>
    </PanelBar>
  );
}
